mapper.select:
select d.* , c.name as categoryName from dish d left join category c on c.id = d.category_id

    and d.category_id = #{categoryId}
    and d.status = #{status}
    and d.name like concat('%', #{name}, '%')

order by id asc

select * from dish

    and name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and status = #{status}

select count(id) from dish

     and status = #{status} 
     and category_id = #{categoryId}

mapper.update:
update dish
category_id = #{categoryId}, description = #{description}, image = #{image}, name = #{name}, price = #{price}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}
