mapper.select:
select s.*, c.name categoryName from setmeal s left join category c on s.category_id = c.id

    s.category_id = #{categoryId}
    and s.name = #{name}
    and s.status = #{status}

mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, image = #{image}, price = #{price}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select * from setmeal

    and name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and status = #{status}
