mapper.select:
select * from shopping_cart

    dish_id = #{dishId}
    and setmeal_id = #{setmealId}
    and user_id = #{userId}

mapper.update:
update shopping_cart set number = #{number}

    and user_id = #{userId}
    and dish_id = #{dishId}
    and setmeal_id = #{setmealId}

update shopping_cart set number = number - 1

    and user_id = #{userId} 
    and dish_id = #{dishId} 
    and dish_flavor = #{dishFlavor}
    and setmeal_id = #{setmealId}
